<?php require_once '../Framework/ControllerBase.php';$split = preg_split('@/@', filter_input(INPUT_SERVER, 'REQUEST_URI'), null, PREG_SPLIT_NO_EMPTY);$elements = count($split);$controller = $elements > 0 ? $split[0] : "Home";$controllerName = strtoupper($controller[0])        . strtolower(substr($controller, 1))        . 'Controller';$controllerFilePath = "../Controller/$controllerName.php";$method = $elements > 1 ? $split[1] : '';$controllerAndMethodInvoked = false;if (file_exists($controllerFilePath)) {  $args = array_slice($split, 2);  require_once $controllerFilePath;  $controllerInstance = new $controllerName;  if (method_exists($controllerInstance, $method)) {    $controllerInstance->$method($args);    $controllerAndMethodInvoked = true;  }}if (!$controllerAndMethodInvoked) {  require_once '../Controller/PageController.php';  $pageController = new PageController;  $pageController->Fetch($split);}