<?phprequire_once '../Framework/ControllerBase.php';$split = preg_split('@/@', filter_input(INPUT_SERVER, 'REQUEST_URI'), null, PREG_SPLIT_NO_EMPTY);$elements = count($split);$controller = $elements > 0 ? $split[0] : "Home";$controllerName = $controller.'Controller';$controllerFilePath = fileExists("../Controller/$controllerName.php");$method = $elements > 1 ? $split[1] : '';$controllerAndMethodInvoked = false;if ($controllerFilePath !== false) {  require_once $controllerFilePath;  $controllerInstance = new $controllerName;  if (method_exists($controllerInstance, $method)) {    $controllerInstance->$method(array_slice($split, 2));    $controllerAndMethodInvoked = true;  } else if (method_exists($controllerInstance, 'Index')) {    $controllerInstance->Index(array_slice($split, 1));    $controllerAndMethodInvoked = true;  }}if (!$controllerAndMethodInvoked) {  require_once '../Controller/PageController.php';  $pageController = new PageController;  $pageController->Fetch($split);}function fileExists($fileName, $caseSensitive = true) {  if (file_exists($fileName)) {    return $fileName;  }  if ($caseSensitive) {    return false;  }  $directoryName = dirname($fileName);  $fileArray = glob($directoryName . '/*', GLOB_NOSORT);  $fileNameLowerCase = strtolower($fileName);  foreach ($fileArray as $file) {    if (strtolower($file) == $fileNameLowerCase) {      return $file;    }  }  return false;}